#!/usr/bin/env python3
# -*- coding: utf-8 -*-

# 常用内建模块->struct
# 练习
# 请编写一个bmpinfo.py，可以检查任意文件是否是位图文件，如果是，打印出图片大小和颜色数。

import base64, struct

bmp_data = base64.b64decode('')

def bmp_info(data):
    head_data = data[:30]
    info = struct.unpack('<ccIIIIIIHH', head_data)
    is_windows_bmp = info[0] == b'B' and info[1] == b'M'
    width = info[-4] if is_windows_bmp else 0
    height = info[-3] if is_windows_bmp else 0
    color = info[-1] if is_windows_bmp else 0

    return {
        'width': width,
        'height': height,
        'color': color
    }

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')